import mongoose from "mongoose";

//user
const userSchema = mongoose.Schema({
    first_name: String,
    last_name: String,
    email: String,
    phone_no: String,
    date_of_birth: String,
    gender: String,
    education: [],
    experience: [
        {
            job_title: String,
            no_year: Number
        }
    ],
    manager_name: String,
    leave_status: {
        type: Boolean,
        default: false,
        require: true
    }
});

//login
const loginSchema = mongoose.Schema({
    username: String,
    password: String
})

//leave
const leaveSchema = mongoose.Schema({
    name: String,
    Date: {
        type: Date,
        default: new Date(),
        require: true
    },
    content: String,
    leave_status: {
        type: Boolean,
        default: false,
        require: true
    }
})

//export
export const UserModel = mongoose.model("users", userSchema);
export const LoginModel = mongoose.model("login", loginSchema);
export const LeaveModel = mongoose.model("leave", leaveSchema);

